/**
 * The user object represents someone interacting with the bot within a specific integration. The same person interacting with a bot in slack and messenger will be represented with two different users.
 */
export interface User {
    /**
     * Name of the [User](#schema_user)
     */
    name?: string;
    /**
     * Picture url of the [User](#schema_user)
     */
    pictureUrl?: string;
    /**
     * User data
     */
    data?: {
        [k: string]: any;
    };
    /**
     * Id of the [User](#schema_user)
     */
    id: string;
    /**
     * Creation date of the [User](#schema_user) in ISO 8601 format
     */
    createdAt: string;
    /**
     * Updating date of the [User](#schema_user) in ISO 8601 format
     */
    updatedAt: string;
}
export interface Conversation {
    /**
     * Id of the [Conversation](#schema_conversation)
     */
    id: string;
    /**
     * Creation date of the [Conversation](#schema_conversation) in ISO 8601 format
     */
    createdAt: string;
    /**
     * Updating date of the [Conversation](#schema_conversation) in ISO 8601 format
     */
    updatedAt: string;
}
/**
 * The Message object represents a message in a [Conversation](#schema_conversation) for a specific [User](#schema_user).
 */
export interface Message {
    /**
     * Id of the [Message](#schema_message)
     */
    id: string;
    /**
     * Creation date of the [Message](#schema_message) in ISO 8601 format
     */
    createdAt: string;
    /**
     * Payload is the content type of the message.
     */
    payload: {
        audioUrl: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "audio";
    } | {
        title: string;
        subtitle?: string;
        imageUrl?: string;
        actions: {
            action: "postback" | "url" | "say";
            label: string;
            value: string;
        }[];
        type: "card";
        /**
         * CSS className to apply to the message
         */
        className?: string;
    } | {
        items: {
            title: string;
            subtitle?: string;
            imageUrl?: string;
            actions: {
                action: "postback" | "url" | "say";
                label: string;
                value: string;
            }[];
            /**
             * CSS className to apply to the message
             */
            className?: string;
        }[];
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "carousel";
    } | {
        text: string;
        options: {
            label: string;
            value: string;
        }[];
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "choice";
        disableFreeText?: boolean;
    } | {
        text: string;
        options: {
            label: string;
            value: string;
        }[];
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "dropdown";
    } | {
        fileUrl: string;
        title?: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "file";
    } | {
        imageUrl: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "image";
    } | {
        latitude: number;
        longitude: number;
        address?: string;
        title?: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "location";
    } | {
        markdown: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "markdown";
    } | {
        text: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "text";
    } | {
        videoUrl: string;
        /**
         * CSS className to apply to the message
         */
        className?: string;
        type: "video";
    };
    /**
     * ID of the [User](#schema_user)
     */
    userId: string;
    /**
     * ID of the [Conversation](#schema_conversation)
     */
    conversationId: string;
}
export interface Event {
    /**
     * ID of the [Event](#schema_event).
     */
    id: string;
    /**
     * Creation date of the [Event](#schema_event) in ISO 8601 format
     */
    createdAt: string;
    /**
     * Payload is the content of the event.
     */
    payload: {
        type: "custom";
        data: {
            [k: string]: {
                [k: string]: any;
            };
        };
    } | {
        type: "conversation_started";
        data: {
            [k: string]: {
                [k: string]: any;
            };
        };
    };
    /**
     * ID of the [Conversation](#schema_conversation).
     */
    conversationId: string;
    /**
     * ID of the [User](#schema_user).
     */
    userId: string;
}
